import { Component } from '@angular/core';
import {Fonction} from "../../model/Fonction";
import {Issue} from "../../model/Issue";
import {UserService} from "../../services/user.service";
import {ActivatedRoute} from "@angular/router";
import {IssueService} from "../../services/issue.service";

@Component({
  selector: 'app-issue-details',
  templateUrl: './issue-details.component.html',
  styleUrls: ['./issue-details.component.css']
})
export class IssueDetailsComponent {
  issue !: Issue
  mode:String = 'Edit'
  fonctions = Object.values(Fonction)

  constructor(
    private issueService: IssueService,
    private route: ActivatedRoute ) {
    //this.user=history.state.user;
    const code_issue: Number = Number(this.route.snapshot.paramMap.get("id"))
    this.issueService.getIssueByCode(code_issue).subscribe(issue => this.issue = issue)
    for (let fonction of this.fonctions) {
      console.log(fonction)
    }
  }
  changeMode() {
    if (this.mode==="Edit") {
      this.mode = "Display"
    } else {
      this.mode = "Edit"
    }
  }

  updateIssue() {
    console.log("update")
    this.issueService.update(this.issue).subscribe(()=>this.changeMode());
  }
}
<button type="button" (click)="changeMode()">{{mode}}</button>
<ng-container *ngIf="mode==='Edit' then display else edit"></ng-container>
<ng-template #display>
  <p>Id : {{issue.code}}</p>
  <p>Nom : {{issue.title}}</p>
  <p>Fonction : {{issue.content}}</p>
</ng-template>
<ng-template #edit>
  <form (ngSubmit)="updateIssue()">
    <label for="code">code : </label><input [(ngModel)]="issue.code" id="code" name="code">
    <label for="title">title :</label><input [(ngModel)]="issue.title" id="title" name="title">
    <label for="content">content :</label><input [(ngModel)]="issue.content" id="content" name="content">
    <button type="submit">Envoyer</button><button type="reset">Reset</button>
  </form>
</ng-template>
